# * Extracts the extension from a filename. 
def getExtension(filename):
    for i in range(len(filename) -1, -1, -1):
        if filename[i] == '.': # we loop backwards until we find a period
            return filename[i: ]  # return the slice from the period to the end
    return '' # file has no extension

def checkOwnership(request, username):
    return request.user.is_authenticated and request.user.username == username

# * getting the file path through recursion
from collections import deque
def getPath(folderOrFile, ids:deque, path=''):
    if not folderOrFile:
        return (path, ids)
    path = folderOrFile.name + '/' + path  # i.e current file or folder name + existing path
    ids.appendleft(folderOrFile.id)
    return getPath(folderOrFile.folder, ids, path)

